      let mut_shape = "fa-circle";
      let color = (b) => b ? high_color : low_color;
      let ts_actual = call_type.actual.of_type;
      let ts_expected = call_type.expected.of_type;
      let [a_o, a_m] = "Owned" in ts_actual ? [true, ts_actual.Owned.mutably_bound] : [false, ts_actual.Ref.is_mut];
